//Sorcier.java//Xavier Brosseau//------------------//class Sorcière héritière de personnage//2015-02-06//classe Sorcierpublic class Sorcier extends Personnage{// variables membres//------------------	private String ctype = "inconnue";	private static int NbSorcier=0;	//constructeur//------------	public Sorcier(String Nom, char Sexe)	{		super(Nom,Sexe);//appel le constructeur de personnage		NbSorcier += 1;// incrémente NbSorcier	}		public Sorcier(String Nom, char Sexe, String Type)	{			super(Nom,Sexe);				this.setType(Type);		NbSorcier += 1;	}// méthodes//---------//////////////////getNbSorcier//////////////////	public static int getNbSorciers()	{		return NbSorcier;	}		public void setType(String Type)	{		if (Type.toLowerCase().equals("eau") || //vérifie si le type existe			Type.toLowerCase().equals("feu") ||			Type.toLowerCase().equals("terre") ||			Type.toLowerCase().equals("air"))		{			ctype = Type;		}	}/////////////getType/////////////	public String getType()	{		return ctype;	}///////////////////Print methode///////////////////	public String toString()	{		String Phrase = "Mon nom est " + getNom() + " et je suis ";		if (Character.toUpperCase(getSexe()) == 'M')// phrase au féminin ou au masculin		{			Phrase += "un sorcier";		}		else		{			Phrase += "une sorciere";		}		Phrase += " de type " + ctype;		return Phrase;	}}